export default {
  data: function data() {
    return {
      msg: "脚手架"
    };
  },
  methods: {
    show: function show() {
      console.log("执行 show 方法");
    },
    beforeCreate: function beforeCreate() {
      //声明周期第一个函数，实例被创建之前会执行它
      //注意：执行函数时data和methods还没有别初始化
      console.log(this.msg);
      this.show();
    },
    created: function created() {
      //生命周期第二函数，实例被创建之后
      //注意：执行函数时data和method初始化好了
      this.show();
      console.log(this.msg);
    },
    beforeMOunt: function beforeMOunt() {
      //生命周期第三个函数，模板已经在内存中编辑完成，但是尚未把模板渲染到页面
      var h3 = document.getElementById("h3");
      console.log(h3);
      console.log("3:" + h3.innerText);
    },
    mounted: function mounted() {
      //生命周期第四个函数，模板已经在内存中编辑完成，把模板写到页面结束
      //当执行完mounted表示示例已经完全创建好了
      //如果没有其他的操作，静静躺在内存中一动不动
      var h3 = document.getElementById("h3");
      console.log("4:" + h3.innerText);
    },
    beforeUpdate: function beforeUpdate() {
      //当执行beforedate页面中显示数据旧
      //此时data数据                 新
      console.log("5内存中data变量：this.msg");
      var h3 = document.getElementById("h3");
      console.log("5模板中数据" + h3.innerText);
    },
    updated: function updated() {
      //当执行beforedate页面中显示数据新
      //此时data数据                   新
      console.log("6内存中data变量：this.msg");
      var h3 = document.getElementById("h3");
      console.log("6模板中数据：" + h3.innerText);
    }
  }
};
